import React from 'react';
import Header from '../Header/Header';
import Image from '../../assets/Images/Upload-video-preview.jpg'

class Upload extends React.Component {
    render(){
        return(
            <>
            <Header />

            <main className="main">
                <section className="main__info">
                    <h1 className="main__info-title">Upload Video</h1>
                    <h5 className="main__info-subtitle">Video Thumbnail</h5>
                    <img className="main__info-image" src={ Image } alt="uploaded"/>
                </section>
                <form className="main__form">
                        <label className="main__form__label">Join The Conversation</label>
                        <div className="main__form__div">
                            <textarea className="main__form_textArea" cols="55" id="comment" name="comment" type="comment" placeholder="That was easily the most spectacular BMX moment ever."></textarea>
                            <button className="main__form__button" type="submit">Comment</button>
                        </div>

                    </form>
            </main>
            </>
        );
    }
}
export default Upload;
