import React from 'react';
import Header from './components/Header/Header';
import MainContent from './components/MainContent/MainContent';
import Axios from 'axios';


class App extends React.Component {
  url = 'https://project-2-api.herokuapp.com';
  apiKey= '?api_key=66fa89b3-0fa2-46c4-ae2a-ae3ed0c1eda7';
  state = {
    mainVideos: {comments:[]},
    sideVideos: []
  }

  getVideoList() {
    Axios.get(`${this.url}/videos/${this.apiKey}`)
    .then(response => {
      this.setState({
        sideVideos: response.data,
        id: response.data[0].id
      })
    })
  }

  getMainVideo(id) {
    Axios.get(`${this.url}/videos/${id}/${this.apiKey}`)
    .then (response => {
      this.setState({
        mainVideos: response.data
      })
    })
  }

  componentDidMount() {
    this.getVideoList();
    this.getMainVideo('1af0jruup5gu');
  }
  render() {
    return (
      <div className="App">
        <Header />
        <MainContent mainVideo={this.state.mainVideos} sideVideo={this.state.sideVideos} />
      </div>
    );
  }
}

export default App;
