import React from 'react';
import Axios from 'axios';

import './MainContent.scss';
import Hero from '../Hero/Hero.js';
import MainVideo from '../MainVideo/MainVideo.js';
import Comment from '../Comments/Comment.js';
import SideVideo from '../SideVideo/SideVideo.js';
import Header from '../Header/Header';

class MainContent extends React.Component {
    url = 'http://localhost:8080';

    state = {
        mainVideos: { comments: [] },
        sideVideos: [],
    }

    getVideoList = () => {
        Axios.get(`${this.url}/videos/`)
            .then(response => {
                this.setState({
                    sideVideos: response.data,
                    id: response.data[0].id
                })
            })
    }

    getMainVideo = (id) => {
        if (id) {
            Axios.get(`${this.url}/videos/${id}/`)
                .then(response => {
                    this.setState({
                        mainVideos: response.data
                    })
                })

        }
    }

    postVideo = (id, object) => {
        Axios.post(`${this.url}/videos/${id}/comments/`, object)
            .then(response => {
                console.log(response);
            })
    }
    submitHandler = (data) => {
        const newRequest = {
            "title": data.title,
            "description": data.description
        } 
        this.postVideo(data.id, newRequest)
    }

    componentDidMount() {
        this.getMainVideo('1af0jruup5gu');
        this.getVideoList();
        // this.postVideo(this.props.match.params.id);
    }

    componentDidUpdate(prevProps) {
        //to make the homepage only for the first video 

        if (this.props.match.params.id !== prevProps.match.params.id) {
            this.getMainVideo(this.props.match.params.id);
        }
    }

    render() {
        if (!this.state.mainVideos) {
            return null;
        }
        return (
            <>
                <Header />
                <main className="main">
                    <Hero mainVideo={this.state.mainVideos} />
                    <section className="main__content">
                        <article className="main__content-article">
                            <MainVideo mainVideo={this.state.mainVideos} />
                            <Comment mainVideo={this.state.mainVideos} />
                        </article>
                        <SideVideo sideVideo={this.state.sideVideos} mainVideo={this.state.mainVideos} />
                    </section>
                </main>
            </>
        );

    }
}
export default MainContent;
