import React from 'react';
import './Comment.scss';
import Avatar from '../../assets/Images/Mohan-muruge.jpg';

class Form extends React.Component {
    render() {
        return (
            <section className="main__content-comments-container1">
                <img className="main__content-comments-picture" src={Avatar} alt="Avatar"></img>
                <form className="main__content-comments-form" onSubmit={this.submitHandler}>
                    <label className="main__content-comments-form__label">Join The Conversation</label>
                    <div className="main__content-comments-form__div">
                        <textarea cols="55" id="comment" className="main__content-comments-form_textArea" name="comment" type="comment" placeholder="That was easily the most spectacular BMX moment ever." vlaue={this.state.value}></textarea>
                        <button className="main__content-comments-form__button" type="submit">Comment</button>
                    </div>
                </form>
            </section>

        );
    }

}

export default Form;
